<template>
  <div class="main-page">
    <div class="main-page__file-list">
      <FileList :items="list.info.response.data"></FileList>
    </div>
  </div>
</template>
<script setup lang="ts">
import { useList } from "./composed/proxypath_api";
import FileList from "./components/FileList.vue";
import { onMounted } from "vue";
const list = useList();
function requestApi() {
  list.request(["vitevue", "vitevuesrc"]);
  setTimeout(requestApi, 5000);
}
onMounted(() => {
  requestApi();
});
</script>
