export interface ListResponse {
  data: ListItem[];
}

export interface ListItem {
  option: string;
  config: Config;
  generated_automatically: boolean;
  hash_key: string;
}

export interface Config {
  handler: string;
  root_path: string;
  proxy_path: boolean;
  path_separator: string;
  file_list_path: string;
  upper_case_symbol: string;
  dot_alias: string;
  vite: Vite;
  config_path: string;
}

export interface Vite {
  default_index_template: string;
  default_script_template: string;
  auto_gen_dir: string;
  port: string;
  is_https: boolean;
  host: string;
  generate_ts: boolean;
  public_dir: string;
}
